mapper.select:
select count(*)
from lz_sports.eventitem
left join lz_sports.event e on eventitem.EventID = e.EventID


		AND ItemName like CONCAT('%', #{name}, '%')


		AND EventName = #{type}


		AND RegistrationStart > #{date}

select ItemID as projectId,e.RegistrationStart as start,ItemName as name, grade,limitation,e.RegistrationEnd as end, e.EventID as eventId, EventName as eventName,Eligibility as state, item.start as projectStart,item.end as projectEnd, item.attendance as attendance,item.maxAttendance as maxAttendance from lz_sports.eventitem item left join lz_sports.event e on item.EventID = e.EventID
AND ItemName like CONCAT('%', #{name}, '%')
AND EventName = #{type}
AND RegistrationStart > #{date}
ORDER BY ItemID LIMIT #{pageSize} OFFSET #{currentPage}
