SELECT r.RegistrationID as id, a.athleteID, a.name as athleteName, e.eventID, e.eventname, i.itemID, i.itemName, r.RegistrationTime, r.registrationStatus FROM lz_sports.registration r JOIN lz_sports.athlete a ON r.athleteId = a.athleteId JOIN lz_sports.event e ON r.eventID = e.EventID JOIN lz_sports.eventitem i ON r.ItemID = i.itemID AND e.EventID = i.EventID
AND a.name like CONCAT('%', #{name}, '%')
AND r.registrationStatus = #{status}
AND r.RegistrationTime > #{date}
ORDER BY r.RegistrationTime DESC LIMIT #{limit} OFFSET #{offset}

SELECT count(*) FROM lz_sports.registration r JOIN lz_sports.athlete a ON r.athleteId = a.athleteId JOIN lz_sports.event e ON r.eventID = e.EventID JOIN lz_sports.eventitem i ON r.ItemID = i.itemID AND e.EventID = i.EventID
AND a.name like CONCAT('%', #{name}, '%')
AND r.registrationStatus = #{status}
AND r.RegistrationTime > #{date}

SELECT r.RegistrationID as id, a.athleteID, a.name as athleteName, e.eventID, e.eventname, i.itemID, i.itemName,
r.RegistrationTime, r.registrationStatus
FROM lz_sports.registration r
JOIN lz_sports.athlete a ON r.athleteId = a.athleteId
JOIN lz_sports.event e ON r.eventID = e.EventID
JOIN lz_sports.eventitem i ON r.ItemID = i.itemID AND e.EventID = i.EventID


		AND a.name like CONCAT('%', #{name}, '%')


		AND r.registrationStatus = #{status}


		AND r.RegistrationTime > #{date}


		AND r.athleteId = #{id}



ORDER BY r.RegistrationTime DESC
LIMIT #{pageSize} OFFSET #{offset}

