mapper.select:
		SELECT u.UserID as id,u.registerTime,Username as name,Status as state,UserType as type,COALESCE(a.applyTime, -1)  as applyState
        from lz_sports.user u
		LEFT JOIN lz_sports.Athlete a ON u.UserID = a.UserID


				AND Username like CONCAT('%', #{name}, '%')


				AND Usertype = #{type}


				AND registerTime > #{date}


		ORDER BY registerTime
		LIMIT #{pageSize} OFFSET #{currentPage}

SELECT COUNT(*)
from lz_sports.user


		AND Username like CONCAT('%', #{name}, '%')


		AND Usertype = #{type}


		AND registerTime >= #{date}
